import React from 'react'
import './Header.scss';
import {APPLICATION_ROUTES} from'../../constants/constants'
import {Link,withRouter} from 'react-router-dom';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from'@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from  '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import { withStyles } from '@material-ui/core/styles';
//ICONS
import MenuIcon from '@material-ui/icons/Menu';
import EmailIcon from '@material-ui/icons/Email';
import InboxIcon from '@material-ui/icons/Inbox';


const styles= theme=>({
    header:{
        marginBottom:-22,
        paddingTop:5,
        paddingBottom:5
    },
    menuIcon:{

    },
    menuText:{
        margin:2
    }

});

export default withRouter(withStyles(styles)(function Header(props) {
    const currentRoute=props.location.pathname.replace('/','');
    const {classes}= props;
    return (
        <React.Fragment>
            <AppBar position="static" className={classes.header}>
                <Toolbar>
                    <IconButton edge="start" color="inherit" aria-label="menu-button" >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h5">
                        Someone
                    </Typography>
                </Toolbar>
            </AppBar>
            <Drawer open={true}>
                <div 
                     role="presentation"
                >
                    <List>
                      {APPLICATION_ROUTES.map((rt,i)=>{
                        return(
                            <ListItem button key={rt.title} selected={currentRoute===rt.title} >
                                <ListItemIcon className={classes.menuIcon}><EmailIcon fontSize={"2em"} /></ListItemIcon>
                                <ListItemText className={classes.menuText} primary={rt.title} />   
                            </ListItem>
                          );
                      })}
                    </List>
                </div>
            </Drawer>
        </React.Fragment>

    )

}));
