import { TOGGLE_MENU,SET_LOADER } from "../actions/actionTypes";

const initialState = {
  menuOpen:false,
 loading:false
};

export default function(state = initialState, action) {
  switch (action.type) {
    case TOGGLE_MENU:
      return { ...state, loading:!state.menuOpen };
    case SET_LOADER:
      return { ...state, loading:action.content.loading };
    default:
      return state;
  }
}
